templateNodes.templateLQL:
`SELECT 
/* always include lhub_score in the final output */
lhub_score,

/* format the final JSON output */
TO_JSON(STRUCT(

/* Required/Standard fields first */
lhub_score, alert_time,
lh_batch_end, lh_batch_id, lh_hostname, lh_alert_id,
alert_id, alert_type, technique, tactic, risk_score, fidelity_score,

/* All other fields EXCEPT additional_data */
attack_chain, dest_ip, dest_machine_name, dest_machine_fqdn, dest_nt_domain, dest_port, dest_user, src_ip, src_machine_name, src_machine_fqdn, src_nt_domain, src_port, src_user,

/* place additional_data last so it will be at the bottom of the JSON formatted output */
additional_data)) AS alert_output
FROM validate_required_input`
