body.messageBody:
{
    "type": "AdaptiveCard",
    "body":[
{
  "columns": [
    {
      "items": [
  {
    "size": "large",
    "text": "Breached Account(s) - Azure Sentinel",
    "type": "TextBlock",
    "weight": "bolder",
    "wrap": true
  },
  {
    "text": "Incident Severity: @{triggerBody()?['object']?['properties']?['severity']}",
    "type": "TextBlock",
    "weight": "Bolder",
    "wrap": true
  },
  {
    "text": " Incident Title: @{triggerBody()?['object']?['properties']?['title']}",
    "type": "TextBlock",
    "weight": "Bolder",
    "wrap": true
  },
  {
    "text": " Incident #: @{triggerBody()?['object']?['properties']?['incidentNumber']} ",
    "type": "TextBlock",
    "weight": "Bolder",
    "wrap": true
  },
  {
    "text": "Incident Description: ",
    "type": "TextBlock",
    "weight": "Bolder",
    "wrap": true
  },
  {
    "text": "@{triggerBody()?['object']?['properties']?['description']}",
    "type": "TextBlock",
   "wrap": true
  },
  {
    "text": "[Click here to view the Incident](@{triggerBody()?['object']?['properties']?['incidentUrl']})",
    "type": "TextBlock",
    "wrap": true
  },
  {
    "size": "Small",
    "type": "Image",
    "url": "https://www.bing.com/th?id=OIP.bhGpY9L2aqT2B00nX_gOoAEiEi&w=93&h=100&c=8&rs=1&qlt=90&dpr=1.5&pid=3.1&rm=2"
  },
  {
    "text": "Breached Account(s):",
    "type": "TextBlock",
    "weight": "Bolder"
  }
],
      "type": "Column"
    }
  ],
  "type": "ColumnSet"
},
  {
    "text": "@{outputs('Compose_Account_names')}",
    "type": "TextBlock",
    "wrap": true
  },

        {
            "type": "ColumnSet",
            "columns": [
                {
                    "type": "Column",
                    "items": [
                        {
                            "type": "TextBlock",
                            "size": "Medium",
                            "weight": "Bolder",
                            "text": "Incident Configuration:",
                            "wrap": true
                        }
                    ],
                    "width": "auto"
                }
            ]
        },
        {
            "type": "ColumnSet",
            "columns": [
                {
                    "type": "Column",
                    "items": [
                        {
                            "type": "Image",
                            "style": "Person",
                            "url": "https://connectoricons-prod.azureedge.net/releases/v1.0.1391/1.0.1391.2130/azuresentinel/icon.png",
                            "size": "Small"
                        }
                    ],
                    "width": "auto"
                }
            ]
        },
        {
            "type": "TextBlock",
            "text": "Close Azure Sentinal incident?"
        },
        {
            "choices": [
                {
                    "isSelected": true,
                    "title": "False Positive - Inaccurate Data",
                    "value": "False Positive - Inaccurate Data"
                },
                {
                    "isSelected": true,
                    "title": "False Positive - Incorrect Alert Logic",
                    "value": "False Positive - Incorrect Alert Logic"
                },
                {
                    "title": "True Positive - Suspicious Activity",
                    "value": "True Positive - Suspicious Activity"
                },
                {
                    "title": "Benign Positive - Suspicious But Expected",
                    "value": "Benign Positive - Suspicious But Expected"
                },
                {
                    "title": "Undetermined",
                    "value": "Undetermined"
                }
            ],
            "id": "incidentStatus",
            "style": "compact",
            "type": "Input.ChoiceSet",
            "value": "Benign Positive - Suspicious But Expected"
        },
        {
            "type": "TextBlock",
            "text": "Change Azure Sentinel Incident Severity?"
        },
        {
            "choices": [
                {

                    "title": "High",
                    "value": "High"
                },
                {
                    "title": "Medium",
                    "value": "Medium"
                },
                {
                    "title": "Low",
                    "value": "Low"
                },
                {
                    "title": "Don't change",
                    "value": "same"
                }
            ],
            "id": "incidentSeverity",
            "style": "compact",
            "type": "Input.ChoiceSet",
            "value": "@{triggerBody()?['object']?['properties']?['severity']}"
        }
],
	"actions": [
{
  "title": "Send Email and Update Incident Configuration",
  "type": "Action.Submit"
},
{
  "title": "Ignore",
  "type": "Action.Submit"
}
],
    "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
    "version": "1.2"
}
